-- Test St_Clip vs St_intersection
DROP FUNCTION news_clip;
CREATE OR REPLACE FUNCTION news_clip (start_year INT, end_year INT, geom geometry)
	RETURNS TABLE (
		clip raster
)
AS $$
BEGIN
RETURN QUERY
    SELECT st_clip(ST_setsrid(air_temp.rast,4326), geom)
        FROM news_test.public.air_temperature_50x50 air_temp
    WHERE ST_Intersects(ST_setsrid(air_temp.rast,4326), geom)
        AND air_temp.year BETWEEN start_year AND end_year;
END; $$
LANGUAGE 'plpgsql';

